import React, { Component } from "react";
var Fontawsome = require("react-fontawsome");
class MovieDetail extends Component {
  render() {
    const mov = this.props.movie;
    return (
      <>
        {mov ? (
          <div>
            <h3>mov.title</h3>
            <Fontawsome
              name="star"
              className={mov.avg_rating > 0 ? "orange" : ""}
            />
            <Fontawsome
              name="star"
              className={mov.avg_rating > 1 ? "orange" : ""}
            />
            <Fontawsome
              name="star"
              className={mov.avg_rating > 2 ? "orange" : ""}
            />
            <Fontawsome
              name="star"
              className={mov.avg_rating > 3 ? "orange" : ""}
            />
            <Fontawsome
              name="star"
              className={mov.avg_rating > 4 ? "orange" : ""}
            />

            <h3>this.props.movie.description</h3>
          </div>
        ) : null}
      </>
    );
  }
}
export default MovieDetail;
