import React from "react";
var Fontawsome = require("react-fontawsome");

function MovieList(props) {
  const movieClicked = (movie) => (eve) => {
    props.movieClicked(movie);
  };
  const movieDelete = (movie) => (eve) => {
    fetch(
      `${process.env.REACT_APP_API_URL}/api/movies/${this.props.movie.id}/`,
      {
        method: "DELETE",
        headers: {
          content_type: "application/json",
          Authorization: "cjsk",
        },
      }
    )
      .then((resp) => resp.json())
      .catch((error) => console.log(error));
  };
  return (
    <div>
      {props.movies.map((movie) => {
        return (
          <div key={movie.id}>
            <h3 onClick={movieClicked(movie)}>{movie.title}</h3>
            <Fontawsome name="edit" />
            <Fontawsome name="trash" onClick={movieDelete(movie)} />
          </div>
        );
      })}
    </div>
  );
}
export default MovieList;
